function getClickedRegion(regionArray, clickX, clickY) {
    var regionId;
    for (var i = 0; i < 45; i++) {
        for (var j = 0; j < 36; j++) {
            if (regionArray[i][j].x === clickX && regionArray[i][j].y === clickY) {
                regionId = regionArray[i][j].name;
            }
        }
    }
    return regionId;
}
